
    var form = document.getElementById('login_form');
    form.onsubmit = function(event) {
        event.preventDefault;
        var formData = new FormData(form);
        var xhr = new XMLHttpRequest();
        // Add any event handlers here...
        xhr.open('POST','page/login_authentication', true);
        xhr.upload.addEventListener("progress", function(e){
            document.getElementById('status').innerHTML='Loading...';
        });
        xhr.onreadystatechange=function(){
            console.log(this.responseText);
            if(this.responseText!=''){
                var response_parsed = JSON.parse(this.responseText);
                if(response_parsed.status=='LOGIN_FAIL'){
                    
                    document.getElementById('status').innerHTML='Username or Password was incorrect...';
                }

            }

        };
        xhr.send(formData);
        return false; // To avoid actual submission of the form
    }




